DROP TABLE IF EXISTS `review`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `review` (
  `reviewNo` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '리뷰글번호',
  `reviewTitle` varchar(500) DEFAULT NULL COMMENT '리뷰제목',
  `reviewContent` text DEFAULT NULL COMMENT '리뷰본문',
  `memberId` varchar(400) DEFAULT NULL COMMENT '작성자',
  `reviewRegdate` datetime DEFAULT current_timestamp() COMMENT '작성시간',
  `reviewUpdatedate` datetime DEFAULT current_timestamp() COMMENT '수정시간',
  `movieRecommend` char(1) DEFAULT NULL COMMENT '영화추천여부',
  `movieNum` bigint(20) DEFAULT NULL COMMENT '영화번호',
  PRIMARY KEY (`reviewNo`),
  KEY `user_id` (`memberId`),
